class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-push-comment",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comment/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comment-${e}`).prepend(o),t.deleteComment($(" .delete-push-comment",o)),new ToggleLike($(" .likebutton",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                        <p>\n                            \n                            <small>\n                                <a class="delete-push-comment" href="/comment/destroy/${e._id}">X</a>\n                            </small>\n                            \n                            ${e.content}\n                            <br>\n                            <small>\n                                ${e.user.name}\n                            </small>\n                            <small>\n                            \n                                <a class="likebutton" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                <i class="fa fa-heart"></i>  0 Likes\n                                </a>\n                            \n                            </small>\n\n                        </p>    \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.post_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}